import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
    name = '';
    price = '';
    quantity  = '';
    products: any[] = [];

    addProduct(){
      const newProduct = {
        name : this.name,
        price : this.price,
        quantity : this.quantity 
      };
      this.products.push(newProduct)
      console.log(this.products);
    }
  }
