import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
    products: any[] = [];
    addProduct(name:string,price:number,quantity:number){
      const newProduct = {
        name,
        price,
        quantity
      };
      this.products.push(newProduct);
    }
  }
