import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-productCard',
  templateUrl: './product-card.component.html',
  styleUrls: ['./product-card.component.css']
})
export class ProductCardComponent {
 @Input() products : any[] = []; //using @input to get value from Parent(appcomp) where we have the products array which consists of products
 //so AppComp sends the product array to productCard which is the child
}
<div class = "card-container">
    <div class = "product-card" *ngFor = "let product of products; let i = index"> 
        <!-- product is the item that we're gonna use for each product and products is the array of products in productCardComp.ts-->
        <!-- the products array creation and pushing of created products occur in the AppComp.ts file and the products array is then sent to the producCardComp.ts-->
         <p class="product-details">Name : {{product.name}}</p>
         <p class="product-details">Price : {{product.price}}</p>
         <p class="product-details ">Quantity : {{product.quantity}}</p>

         <div class = "btn-grp">
            <button class = "edit-btn" (click) = "editProduct(product)">Edit</button>
            <button class = "delete-btn" (click) = "deleteProduct(index)">Delete</button>
         </div>
    </div>
</div>
