import { Component, Inject } from '@angular/core';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';

@Component({
  selector: 'app-edit-product-dialog',
  templateUrl: './edit-product-dialog.component.html',
  styleUrls: ['./edit-product-dialog.component.css']
})
export class EditProductDialogComponent {
  constructor(
    public dialogRef: MatDialogRef<EditProductDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  saveChanges() {
    this.dialogRef.close(this.data); // Pass updated data back
  }
  isFormValid(): boolean {
    return this.data?.name?.trim() !== '' && this.data?.price !== null && this.data?.quantity !== null;
  }

}
<h2 mat-dialog-title class="dialog-title">Edit Product</h2>

<div mat-dialog-content class="dialog-content">

  <mat-form-field class="custom-field" appearance="outline">
    <mat-label>Product Name</mat-label>
    <input matInput [(ngModel)]="data.name" required>
  </mat-form-field>

  <mat-form-field class="custom-field" appearance="outline">
    <mat-label>Price</mat-label>
    <input matInput type="number" [(ngModel)]="data.price" required>
  </mat-form-field>

  <mat-form-field class="custom-field" appearance="outline">
    <mat-label>Quantity</mat-label>
    <input matInput type="number" [(ngModel)]="data.quantity" required>
  </mat-form-field>

</div>

<div mat-dialog-actions class="dialog-actions">
  <button mat-button (click)="dialogRef.close()">Cancel</button>
  <button mat-raised-button color="primary" (click)="saveChanges() [disabled] = !isFormValid()">Save</button>
</div>
